import React from 'react'
import { Tabs, Select, Button, Form, Input, Drawer } from 'antd'


const { Option } = Select;
const { TabPane } = Tabs;

const DeviceDrawer = (props) => {

  const [activeTab, setActiveTab] = React.useState(1);

  const tabs = { 1: "addRoomForm", 2: "addCompForm" };

  const handleSubmit = async (values) => {
    console.log(values);
    var requestError = false;
    switch (values.device) {
      case "light":
        await addLight(
          JSON.stringify({ room: values.room, name: values.lightName })
        );
        break;
      case "thermostat":
        await addThermostat(JSON.stringify({ room: values.room })).catch(
          (error) => {
            message.error("Another thermostat already exists!");
            requestError = true;
          }
        );
        break;
      default:
        break;
    }
    if (values.room === currentRoomID) {
      changeRoom(values.room);
    }
    if (!requestError) setAddCompOpen(false);
    props.displayLightName.setDisplayLightName(false);
  };

  const handleTabs = (id) => {
    setActiveTab(id);
  };

  return (
    <Drawer
      width={360}
      onClose={(e) => {
        props.open.setIsOpen(false);
        props.displayLightName.setDisplayLightName(false);
      }}
      visible={props.open.isOpen}
      destroyOnClose="true"
      bodyStyle={{ paddingTop: 5 }}
      footer={
        <div
          style={{
            textAlign: "right",
          }}
        >
          <Button
            onClick={(e) => {
              props.open.setIsOpen(false);
              props.displayLightName.setDisplayLightName(false);
            }}
            style={{ marginRight: 8 }}
          >
            Cancel
          </Button>
          <Button
            type="primary"
            htmlType="submit"
            form={tabs[activeTab]}
          >
            Submit
          </Button>
        </div>
      }
    >
      <Tabs defaultActiveKey="1" onChange={handleTabs}>
        <TabPane tab="Add room" key="1">
          <Form
            layout="vertical"
            onFinish={addRoomHandle}
            onFinishFailed={(e) => props.open.setIsOpen(true)}
            id="addRoomForm"
          >
            <Form.Item
              name="room"
              label="Room"
              rules={[
                {
                  required: true,
                  message: "Please choose a name for your room",
                },
              ]}
            >
              <Input placeholder="Room name" />
            </Form.Item>
          </Form>
        </TabPane>
        <TabPane tab="Add device" key="2">
          <Form
            layout="vertical"
            onFinish={handleSubmit}
            onFinishFailed={(e) => props.open.setIsOpen(true)}
            id="addCompForm"
          >
            <Form.Item
              name="room"
              label="Room"
              rules={[
                {
                  required: true,
                  message: "Please select a room to add",
                },
              ]}
            >
              <Select placeholder="Select the room">
                {props.rooms.map((room) => {
                  return (
                    <Option key={room.id} value={room.id}>
                      {room.name.charAt(0).toUpperCase() +
                        room.name.slice(1)}
                    </Option>
                  );
                })}
              </Select>
            </Form.Item>
            <Form.Item
              name="device"
              label="Smart device type"
              rules={[
                {
                  required: true,
                  message: "Please select a device to add",
                },
              ]}
            >
              <Select
                placeholder="Select the smart device type"
                onChange={(value) => {
                  value === "light"
                    ? props.displayLightName.setDisplayLightName(true)
                    : props.displayLightName.setDisplayLightName(false);
                }}
              >
                <Option value="light">Light</Option>
                <Option value="thermostat">Thermostat</Option>
              </Select>
            </Form.Item>
            {props.displayLightName.displayLightName ? (
              <Form.Item
                name="lightName"
                label="Light Name"
                rules={[
                  {
                    required: true,
                    message: "Please choose a name for your light",
                  },
                ]}
              >
                <Input placeholder="Light name" />
              </Form.Item>
            ) : (
              false
            )}
          </Form>
        </TabPane>
      </Tabs>
    </Drawer>
  )
}

export default DeviceDrawer
