import React from "react";
import Light from "../components/Light";
import Thermostat from "../components/Thermostat";
import {
  recognizeAudio,
  getRooms,
  getRoomByID,
  addLight,
  addThermostat,
  addRoom,
  deleteRoom,
  analyzePhrase,
} from "../Request";
import {
  Layout,
  Menu,
  Spin,
  Button,
  Form,
  Input,
  message,
  Modal,
} from "antd";
import DeviceDrawer from '../components/DeviceDrawer'
import { ReactMic } from "react-mic";

import "../styles/dashboard.css";

import {
  MenuOutlined,
  HomeOutlined,
  PlusOutlined,
  DeleteOutlined,
  CloseOutlined,
  MinusCircleTwoTone,
  MessageOutlined,
} from "@ant-design/icons";

const { TextArea } = Input;
const { Header, Sider, Content } = Layout;


const Dashboard = () => {
  
  const [collapsed, setCollapsed] = React.useState(false);
  const [available, setAvailable] = React.useState(false);
  const [currentRoomID, setCurrentRoomID] = React.useState();
  const [rooms, setRooms] = React.useState([]);
  const [roomData, setRoomData] = React.useState({});
  const [activeTab, setActiveTab] = React.useState(1);
  const [addCompOpen, setAddCompOpen] = React.useState(false);
  const [delComp, setDelComp] = React.useState(false);
  const [displayLightNameInput, setDisplayLightNameInput] = React.useState(false);
  const [showAnalyze, setShowAnalyze] = React.useState(false);
  const [loadingAnalyze, setLoadingAnalyze] = React.useState(false);
  const [refresh, setRefresh] = React.useState(false);

  //record states
  const [isRecording, setIsRecording] = React.useState(false);
  const [isComputing, setIsComputing] = React.useState(false);

  
  const [form] = Form.useForm();

  const refreshRooms = async () => {
    let response = await getRooms();
    setRooms(response.data);
    return response;
  };

  const roomDelete = async (roomID) => {
    await deleteRoom(roomID);
    await refreshRooms();
  };

  const toggleCollapsed = () => {
    setCollapsed(!collapsed);
  };

  const handleTabs = (id) => {
    setActiveTab(id);
  };

  const changeRoom = async (roomID) => {
    let tmp = await getRoomByID(roomID);
    setRoomData(tmp.data);
    setCurrentRoomID(tmp.data.id);
    setRefresh(!refresh);
  };

  const handleAnalyze = async (values) => {
    setLoadingAnalyze(true);
    let res = await analyzePhrase(JSON.stringify({ phrase: values.phrase }));
    if (res.data.ERROR) {
      message.error("I didn't understand!");
    } else {
      setShowAnalyze(false);
      if (res.data.roomID === currentRoomID) changeRoom(res.data.roomID);
      else if (res.data.roomID === 0) changeRoom(currentRoomID);
      message.success("Command executed");
    }
    setLoadingAnalyze(false);
  };

  const handleSubmit = async (values) => {
    console.log(values);
    var requestError = false;
    switch (values.device) {
      case "light":
        await addLight(
          JSON.stringify({ room: values.room, name: values.lightName })
        );
        break;
      case "thermostat":
        await addThermostat(JSON.stringify({ room: values.room })).catch(
          (error) => {
            message.error("Another thermostat already exists!");
            requestError = true;
          }
        );
        break;
      default:
        break;
    }
    if (values.room === currentRoomID) {
      changeRoom(values.room);
    }
    if (!requestError) setAddCompOpen(false);
    setDisplay(false);
  };

  const addRoomHandle = async (values) => {
    var requestError = false;
    await addRoom(JSON.stringify({ name: values.room.toLowerCase() })).catch(
      (error) => {
        message.error("A room with the same name already exists!");
        requestError = true;
      }
    );
    await refreshRooms();
    if (!requestError) setAddCompOpen(false);
    setDisplay(false);
  };

  const renderRoomComponents = (component) => {
    if (Object.keys(roomData).length !== 0 && roomData.constructor === Object) {
      switch (component) {
        case "light":
          return roomData.lights.map((item) => {
            return (
              <Light
                key={item.id}
                data={item}
                delete={delComp}
                refresh={refresh}
              />
            );
          });
        case "thermostat":
          return roomData.thermostats.map((item) => {
            return (
              <Thermostat
                key={item.id}
                data={item}
                delete={delComp}
                refresh={refresh}
              />
            );
          });
        default:
          break;
      }
    }
  };

  const audioRecognize = async (audio) => {
    setIsComputing(true);
    console.log(audio);
    var fd = new FormData();
    fd.append("audio", audio.blob);
    await recognizeAudio(fd).then((res) => {
      console.log(res.data);
      form.setFieldsValue({
        phrase: res.data.transcription,
      });
      setIsComputing(false);
    });
  };

  React.useEffect(() => {
    async function initialSetup() {
      let tmpRooms = await refreshRooms();
      if (tmpRooms.data.length > 0) {
        setCurrentRoomID(tmpRooms.data[0].id);
        await changeRoom(tmpRooms.data[0].id);
      }
      setAvailable(true);
    }
    initialSetup();
  }, []);

  return (
    <>
      {available === false ? (
        <Spin size="large" className="loadBox" />
      ) : (
        <Layout style={{ height: "100%", flexWrap: "wrap" }}>
          <Sider
            trigger={null}
            collapsible
            collapsed={collapsed}
            collapsedWidth={0}
            style={{ overflow: "auto" }}
          >
            <div
              style={{
                padding: "20px 24px",
                display: "flex",
                flexDirection: "row",
                alignItems: "flex-end",
              }}
            >
              <HomeOutlined style={{ fontSize: "25px", color: "#ffffff" }} />
              <p
                style={{
                  color: "white",
                  padding: "0px 10px",
                  margin: 0,
                  fontSize: "bold",
                }}
              >
                <strong>Rooms</strong>
              </p>
            </div>
            <Menu
              theme="dark"
              mode="inline"
              defaultSelectedKeys={[String(currentRoomID)]}
              selectable={!delComp}
              onClick={(e) => {
                if (!delComp) {
                  changeRoom(e.key);
                  setDelComp(false);
                }
              }}
            >
              {rooms.length > 0
                ? rooms.map((room) => {
                    return (
                      <Menu.Item
                        key={room.id}
                        style={{
                          margin: 0,
                          display: "flex",
                          flexDirection: "row",
                          justifyContent: "space-between",
                          alignItems: "center",
                        }}
                      >
                        {room.name.charAt(0).toUpperCase() + room.name.slice(1)}
                        {delComp ? (
                          <Button
                            size="small"
                            danger
                            type="text"
                            icon={
                              <MinusCircleTwoTone
                                twoToneColor="#ff0000"
                                style={{ marginRight: 0 }}
                              />
                            }
                            onClick={(e) => {
                              roomDelete(room.id);
                            }}
                          />
                        ) : (
                          false
                        )}
                      </Menu.Item>
                    );
                  })
                : false}
            </Menu>
          </Sider>
          <Layout className="site-layout">
            <Header
              className="site-layout-background"
              style={{
                padding: 0,
                display: "flex",
                flexDirection: "row",
                alignItems: "center",
              }}
            >
              {React.createElement(collapsed ? MenuOutlined : MenuOutlined, {
                className: "trigger",
                onClick: toggleCollapsed,
              })}
              <h2 style={{ fontWeight: "bold" }}>
                {Object.keys(roomData).length !== 0 &&
                roomData.constructor === Object
                  ? roomData.name.charAt(0).toUpperCase() +
                    roomData.name.slice(1)
                  : false}
              </h2>
            </Header>
            <Content
              className="site-layout-background"
              style={{
                margin: "16px 16px",
                display: "flex",
                flexDirection: "row",
                background: "#f0f2f5",
                flexWrap: "wrap",
                overflow: "auto",
              }}
            >
              {renderRoomComponents("light")}
              {renderRoomComponents("thermostat")}
              <Modal
                title="Type a command to execute"
                visible={showAnalyze}
                footer={false}
                onCancel={(e) => {
                  !loadingAnalyze && setShowAnalyze(false);
                }}
                bodyStyle={{ padding: "24px" }}
                destroyOnClose="true"
              >
                <Form id="analyzeForm" onFinish={handleAnalyze} form={form}>
                  <Form.Item
                    name="phrase"
                    rules={[
                      {
                        required: true,
                        message: "Please insert a command to execute",
                      },
                    ]}
                  >
                    <TextArea
                      rows={4}
                      allowClear="true"
                      placeholder="e.g. Turn on Kitchen lights"
                    />
                  </Form.Item>
                  <ReactMic
                    record={isRecording}
                    className="audio-wave"
                    mimeType="audio/webm"
                    onStop={(audioRecorded) => audioRecognize(audioRecorded)}
                    noiseSuppression={true}
                  />
                  <div
                    style={{ display: "flex", justifyContent: "space-between" }}
                  >
                    <Button
                      onClick={(e) => setIsRecording(!isRecording)}
                      type="primary"
                      loading={isComputing}
                    >
                      {!isRecording ? "Record" : "Stop Recording"}
                    </Button>
                    <Form.Item
                      style={{
                        textAlign: "right",
                        marginRight: 10,
                        marginBottom: 0,
                      }}
                    >
                      <Button
                        type="default"
                        onClick={(e) => {
                          !loadingAnalyze && setShowAnalyze(false);
                        }}
                        style={{ marginRight: 10 }}
                      >
                        Cancel
                      </Button>
                      <Button
                        type="primary"
                        htmlType="submit"
                        loading={loadingAnalyze}
                      >
                        Submit
                      </Button>
                    </Form.Item>
                  </div>
                </Form>
              </Modal>
              <Button
                type="primary"
                shape="circle"
                size={"large"}
                className="commandBtn"
                onClick={(e) => setShowAnalyze(true)}
              >
                <MessageOutlined />
              </Button>
              <Button
                type="primary"
                shape="circle"
                size={"large"}
                className="delBtn"
                danger
                onClick={(e) => setDelComp(!delComp)}
              >
                {delComp ? <CloseOutlined /> : <DeleteOutlined />}
              </Button>
              <Button
                type="primary"
                shape="circle"
                size={"large"}
                className="addBtn"
                onClick={(e) => {
                  setAddCompOpen(true);
                  setDelComp(false);
                }}
              >
                <PlusOutlined />
              </Button>
              <DeviceDrawer 
                open = {{isOpen: addCompOpen, setIsOpen: setAddCompOpen}}
                displayLightName = {{}}
              />
            </Content>
          </Layout>
        </Layout>
      )}
    </>
  );
};

export default Dashboard;
